                            "value": "{\"blocks\":[{\"key\":\"feqs9\",\"text\":\"En esta colecci\u00f3n encontrar\u00e1s:\",\"type\":\"header-one\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":30,\"style\":\"BOLD\"}],\"entityRanges\":[],\"data\":{}},{\"key\":\"8cbu7\",\"text\":\"Los ni\u00f1os, ni\u00f1as y adolescentes le hablan a la Comisi\u00f3n de la Verdad\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[{\"offset\":0,\"length\":68,\"key\":0}],\"data\":{}},{\"key\":\"diul\",\"text\":\"Traves\u00edas por la Verdad\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[{\"offset\":0,\"length\":23,\"key\":1}],\"data\":{}},{\"key\":\"bm74q\",\"text\":\"Entretejiendo Relatos de Verdad\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[{\"offset\":0,\"length\":31,\"key\":2}],\"data\":{}}],\"entityMap\":{\"0\":{\"type\":\"LINK\",\"mutability\":\"MUTABLE\",\"data\":{\"url\":\"#section2\"}},\"1\":{\"type\":\"LINK\",\"mutability\":\"MUTABLE\",\"data\":{\"url\":\"#section10\"}},\"2\":{\"type\":\"LINK\",\"mutability\":\"MUTABLE\",\"data\":{\"url\":\"#section18\"}}}}",
