                            "value": "{\"blocks\":[{\"key\":\"2cqk7\",\"text\":\"Organizaci\u00f3n Agroarte Colombia\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":30,\"style\":\"BOLD\"}],\"entityRanges\":[],\"data\":{}},{\"key\":\"9u91q\",\"text\":\"Medell\u00edn, Antioquia \",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":20,\"style\":\"BOLD\"}],\"entityRanges\":[],\"data\":{}},{\"key\":\"44tck\",\"text\":\"\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}},{\"key\":\"6tqje\",\"text\":\"Acompa\u00f1amos los duelos que por la violencia viven las personas. De la siembra conjunta ha crecido la hermandad. Creamos acciones de memoria y resistencia.\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}}],\"entityMap\":{}}",
